#!/usr/bin/env bash

function check_and_AUR() {
	local package="$1"
	local aur_helper

	# Check for AUR helper
	if type yay &>/dev/null; then
		aur_helper="yay"
	elif type paru &>/dev/null; then
		aur_helper="paru"
	else
		echo "No AUR helper found. You will need one to install AUR packages."
		read -p "Do you want to install yay? (Y/n) " REPLY
		echo
		if [[ $REPLY =~ ^[Yy]$ ]] || [[ -z $REPLY ]]; then
			echo "Installing yay into $USER_DIR/AUR-helpers..."
			mkdir -p "$USER_DIR"/AUR-helpers && git \
				-C "$USER_DIR"/AUR-helpers clone https://aur.archlinux.org/yay.git &&
				cd "$USER_DIR"/AUR-helpers/yay && makepkg -fsri --noconfirm
			cd - # Return to the previous directory
			if [ $? -ne 0 ]; then
				echo "Failed to install yay. Aborting."
				exit 1
			else
				aur_helper="yay"
			fi
		else
			echo "An AUR helper is required to install AUR packages. Aborting."
			exit 1
		fi
	fi
}

CURR_LOCATION="$PWD"
SCRIPT_NAME="install-me.sh"

echo "Copying script ($SCRIPT_NAME) to $CURR_LOCATION"

cat >install-me.sh <<EOF
#!/usr/bin/env bash

# Determine package manager
if command -v yay &> /dev/null; then
    PKG_MANAGER="yay"
elif command -v paru &> /dev/null; then
    PKG_MANAGER="paru"
    echo "Note: yay not found, using paru instead."
else
    echo "Error: Neither yay nor paru found in PATH." >&2
    echo "Please install yay or paru first:" >&2
    echo "  yay: https://github.com/Jguer/yay" >&2
    echo "  paru: https://github.com/Morganamilo/paru" >&2
    exit 1
fi

# Check for gen_log function/command
if ! command -v gen_log &> /dev/null; then
    echo "Warning: gen_log not found in PATH."
    echo "Command output will not be logged to log file."
    echo "Only command execution will be echoed to console."

    read -p "Do you want to proceed without logging: [y/N] " CONFIRM
    echo
    if [[ ! $CONFIRM =~ ^[Yy]$ ]]; then
        echo "Aborting installation."
        exit 1
    fi

    # Function to replace gen_log with echo + execution
    gen_log() {
        echo "Running: $@"
        "$@"
    }
fi

